import React from 'react';
import { TouchableOpacity, Text } from 'react-native';


export const RoundenButton = ({
    style = {},
    textStyle = {},
    size = 125,
    ...props
}) => {
    return (
        <TouchableOpacity style={[styles(size).radius, style]}>
            <Text style={[styles.text, textStyle]}>
                {props.title}
            </Text>
        </TouchableOpacity>
    )
}

const styles = (size) => 
    StyleSheet.create({
    radius: {
        borderRadius: size / 2,
        width: size,
        height: size,
        alignItems: 'center'
    },
    text: {
        color: '#fff',
        fontSize: 20
    }
})
